<style>

	.loader-wrapper {
		position: relative;
		width: 100vw;
		height: 100vh;
		background-color: var(--ink);
		overflow: hidden;


		& svg {
			position: absolute;
			top: 25vh;
			left: calc((100vw - 300px)/2);
			width: 300px;
		}
	}


	@keyframes fadeInOut {
		0%, 100% { opacity: 0; }
		50% { opacity: 1; }
	}

	.logo-egan, .logo-adison {
		opacity: 0;
		animation: fadeInOut 2000ms infinite;
	}

	.logo-egan {
		animation-delay: 0ms;
	}

	.logo-adison {
		animation-delay: 1000ms; /* Half of the animation duration to ensure they alternate */
}


</style>
